export default {
  name: 'home',
  data: function data() {
    return {
      drawing: false,
      mouse: {
        current: {
          x: 0,
          y: 0
        },
        previous: {
          x: 0,
          y: 0
        },
        down: false
      }
    };
  },
  ready: function ready() {
    var c = document.getElementById("canvas");
    var ctx = c.getContext("2d");
    ctx.translate(0.5, 0.5);
    ctx.imageSmoothingEnabled = false; // this.draw();
  },
  computed: {
    currentMouse: function currentMouse() {
      var c = document.getElementById("canvas");
      var rect = c.getBoundingClientRect();
      return {
        x: this.mouse.current.x - rect.left,
        y: this.mouse.current.y - rect.top
      };
    }
  },
  components: {},
  methods: {
    onMouseDown: function onMouseDown(e) {
      this.drawing = true;
      current.x = e.clientX || e.touches[0].clientX;
      current.y = e.clientY || e.touches[0].clientY;
    },
    draw: function draw(event) {
      // requestAnimationFrame(this.draw);
      if (this.mouse.down) {
        var c = document.getElementById("canvas");
        var ctx = c.getContext("2d");
        ctx.clearRect(0, 0, 800, 800);
        ctx.lineTo(this.currentMouse.x, this.currentMouse.y);
        ctx.strokeStyle = "#F63E02";
        ctx.lineWidth = 2;
        ctx.stroke();
      }
    },
    handleMouseDown: function handleMouseDown(event) {
      this.mouse.down = true;

      if (event.type === 'touchstart') {
        this.mouse.current = {
          x: event.changedTouches[0].clientX,
          y: event.changedTouches[0].clientY
        };
      } else {
        this.mouse.current = {
          x: event.pageX,
          y: event.pageY
        };
      }

      var c = document.getElementById("canvas");
      var ctx = c.getContext("2d");
      ctx.moveTo(this.currentMouse.x, this.currentMouse.y);
    },
    handleMouseUp: function handleMouseUp() {
      this.mouse.down = false; //save current canvas url to store

      var currCanvas = this.$refs.canvas;
      console.log('currCanvas', currCanvas);
      var pngUrl = currCanvas.toDataURL(); // let pngUrl = 'currCanvas.toDataURL()';

      this.$store.dispatch({
        type: 'setCanvasUrl',
        canvasUrl: pngUrl
      });
    },
    handleMouseMove: function handleMouseMove(event) {
      if (event.type === 'touchmove') {
        this.mouse.current = {
          x: event.changedTouches[0].clientX,
          y: event.changedTouches[0].clientY
        };
      } else {
        this.mouse.current = {
          x: event.pageX,
          y: event.pageY
        };
      }

      this.draw(event);
    }
  }
};
